statement-execution-processor.statement:
DECLARE
v_credential_count NUMBER;
BEGIN
SELECT COUNT(*)
INTO v_credential_count
FROM ALL_CREDENTIALS
WHERE CREDENTIAL_NAME = '{0}';

IF v_credential_count = 0 THEN
DBMS_CLOUD.CREATE_CREDENTIAL(
'{1}'
);
END IF;
END;

DECLARE
v_profile_count NUMBER;
BEGIN
SELECT COUNT(*)
INTO v_profile_count
FROM DBA_CLOUD_AI_PROFILES
WHERE PROFILE_NAME = '{0}';

IF v_profile_count = 0 THEN
DBMS_CLOUD_AI.CREATE_PROFILE(
'{1}'
);
END IF;
END;

declare
credential_names VARCHAR2(4000);
BEGIN
SELECT LISTAGG(CREDENTIAL_NAME, ' ') WITHIN GROUP (ORDER BY CREDENTIAL_NAME)
INTO credential_names
FROM ALL_CREDENTIALS;
? := credential_names;
END;

declare
profile_names VARCHAR2(4000);
begin
SELECT LISTAGG(PROFILE_NAME, ' ') WITHIN GROUP ( ORDER BY PROFILE_NAME )
INTO profile_names
FROM USER_CLOUD_AI_PROFILES;
? := profile_names;
end;

declare
query_output VARCHAR2(4000);
begin
SELECT DBMS_CLOUD_AI.GENERATE(prompt
=> '{2}', profile_name => '{0}',
action
=> '{1}')
INTO query_output
FROM dual;
? := query_output;
end;

declare
tables VARCHAR2(4000);
begin
SELECT LISTAGG(TABLE_NAME, ' ') WITHIN GROUP ( ORDER BY TABLE_NAME )
INTO tables
FROM all_tables;
? := tables;
end;
/

declare
views VARCHAR2(4000);
begin
SELECT LISTAGG(VIEW_NAME, ' ') WITHIN GROUP ( ORDER BY VIEW_NAME )
INTO views
FROM all_views;
? := views;
end;

BEGIN
DBMS_CLOUD_AI.SET_ATTRIBUTE(
profile_name    => '{0}',
attribute_name  => '{1}',
attribute_value => '{2}'
);
END;

BEGIN
DBMS_CLOUD.UPDATE_CREDENTIAL(
credential_name => '{0}',
attribute => '{1}}',
value => '{2}}');
END;
